### Advanced Lazy Loading

```javascript
// Intersection Observer based lazy loading with fade-in
class LazyLoader {
  constructor(options = {}) {
    this.options = {
      root: null,
      rootMargin: '50px',
      threshold: 0.1,
      fadeInDuration: 300,
      ...options
    };

    this.observer = new IntersectionObserver(
      this.handleIntersection.bind(this),
      {
        root: this.options.root,
        rootMargin: this.options.rootMargin,
        threshold: this.options.threshold
      }
    );
  }

  observe(elements) {
    elements.forEach(element => {
      this.observer.observe(element);
    });
  }

  handleIntersection(entries) {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        this.loadElement(entry.target);
        this.observer.unobserve(entry.target);
      }
    });
  }

  async loadElement(element) {
    const src = element.dataset.src;
    const srcset = element.dataset.srcset;

    if (element.tagName === 'IMG') {
      await this.loadImage(element, src, srcset);
    } else if (element.tagName === 'IFRAME') {
      element.src = src;
    }

    // Fade in animation
    element.style.transition = `opacity ${this.options.fadeInDuration}ms ease`;
    element.style.opacity = '1';
  }

  loadImage(img, src, srcset) {
    return new Promise((resolve, reject) => {
      const tempImg = new Image();

      tempImg.onload = () => {
        img.src = src;
        if (srcset) img.srcset = srcset;
        img.classList.add('loaded');
        resolve();
      };

      tempImg.onerror = reject;
      tempImg.src = src;
    });
  }
}

// Usage
const lazyLoader = new LazyLoader();
const lazyImages = document.querySelectorAll('img[data-src]');
lazyLoader.observe(lazyImages);
